  --sidebar-primary: 274 68% 59%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 48 100% 90%;

  --sidebar-accent-foreground: 0 0% 13%;

  --sidebar-ring: 274 68% 59%;

  --popover: 0 0% 95%;

  --popover-foreground: 0 0% 13%;

