server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import {
  ObjectAclPolicy,
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

export class ObjectStorageService {
  constructor() {}

  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      const [metadata] = await file.getMetadata();
      const aclPolicy = await getObjectAclPolicy(file);
      const isPublic = aclPolicy?.visibility === "public";

      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(rawPath: string): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

client/src/components/WiFiSpeedTest.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Wifi, Activity, CheckCircle2, AlertCircle } from "lucide-react";

interface WiFiSpeedTestProps {
  value?: number;
  onChange: (speed: number | undefined) => void;
}

export function WiFiSpeedTest({ value, onChange }: WiFiSpeedTestProps) {
  const [isTesting, setIsTesting] = useState(false);
  const [progress, setProgress] = useState(0);

  const handleManualChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const inputValue = e.target.value;
    if (inputValue === '') {
      onChange(undefined);
    } else {
      const numValue = parseInt(inputValue, 10);
      if (!isNaN(numValue) && numValue >= 0) {
        onChange(numValue);
      }
    }
  };

  const runSpeedTest = async () => {
    setIsTesting(true);
    setProgress(0);

    // Simulazione test velocitÃ  (in produzione usare un servizio reale come speedtest.net API o fast.com)
    const simulateTest = async () => {
      // Incrementa progress
      for (let i = 0; i <= 100; i += 10) {
        await new Promise(resolve => setTimeout(resolve, 200));
        setProgress(i);
      }

      // Simula velocitÃ  misurata (in produzione, fare vero test di download)
      const measuredSpeed = Math.floor(Math.random() * 150) + 20; // 20-170 Mbps
      onChange(measuredSpeed);
      setIsTesting(false);
    };

    await simulateTest();
  };

  const getSpeedQuality = (mbps: number) => {
    if (mbps >= 100) return { label: "Eccellente", color: "text-chart-2", icon: CheckCircle2 };
    if (mbps >= 50) return { label: "Buona", color: "text-chart-1", icon: Activity };
    return { label: "Sufficiente", color: "text-yellow-600", icon: AlertCircle };
  };

  const speedQuality = value ? getSpeedQuality(value) : null;
  const SpeedIcon = speedQuality?.icon;

  return (
    <Card className="p-6 space-y-4" data-testid="card-wifi-test">
      <div className="flex items-center gap-3">
        <div className="p-3 bg-primary/10 rounded-full">
          <Wifi className="h-6 w-6 text-primary" />
        </div>
        <div>
          <h3 className="font-semibold">VelocitÃ  WiFi</h3>
          <p className="text-sm text-muted-foreground">
            Inserisci la velocitÃ  manualmente o esegui un test automatico
          </p>
        </div>
      </div>

      <div className="space-y-2">
        <Label htmlFor="wifi-speed-input">VelocitÃ  WiFi (Mbps)</Label>
        <Input
          id="wifi-speed-input"
          type="number"
          min="0"
          max="2000"
          placeholder="Es. 100"
          value={value ?? ''}
          onChange={handleManualChange}
          disabled={isTesting}
          data-testid="input-wifi-speed"
        />
        <p className="text-xs text-muted-foreground">
          VelocitÃ  minima raccomandata: 50 Mbps per smart working
        </p>
      </div>

      {value && !isTesting && speedQuality && SpeedIcon && (
        <div className="bg-muted/50 rounded-lg p-4 text-center space-y-2">
          <div className="flex items-center justify-center gap-2">
            <SpeedIcon className={`h-5 w-5 ${speedQuality.color}`} />
            <span className="text-2xl font-bold" data-testid="text-speed-result">
              {value} Mbps
            </span>
          </div>
          <p className={`text-sm font-medium ${speedQuality.color}`} data-testid="text-speed-quality">
            VelocitÃ  {speedQuality.label}
          </p>
        </div>
      )}

      {isTesting && (
        <div className="space-y-2">
          <div className="flex items-center justify-between text-sm">
            <span>Test automatico in corso...</span>
            <span>{progress}%</span>
          </div>
          <Progress value={progress} className="h-2" data-testid="progress-wifi-test" />
        </div>
      )}

      <Button
        onClick={runSpeedTest}
        disabled={isTesting}
        variant="outline"
        className="w-full"
        data-testid="button-run-wifi-test"
      >
        {isTesting ? "Test in corso..." : value ? "Ripeti Test Automatico" : "Avvia Test Automatico"}
      </Button>

      <p className="text-xs text-muted-foreground text-center">
        {value && value >= 50 
          ? "â WiFi certificato - VelocitÃ  adatta per smart working e streaming"
          : "VelocitÃ  WiFi importante per l'esperienza degli ospiti"}
      </p>
    </Card>
  );
}

